CREATE COLUMN TABLE "TCMP"."CS_PERIOD" ("TENANTID" VARCHAR(4) DEFAULT SESSION_USER, "PERIODSEQ" BIGINT CS_FIXED NOT NULL , "CREATEDATE" LONGDATE CS_LONGDATE NOT NULL , "REMOVEDATE" LONGDATE CS_LONGDATE NOT NULL , "CREATEDBY" VARCHAR(255) NOT NULL , "MODIFIEDBY" VARCHAR(255), "NAME" VARCHAR(50) NOT NULL , "SHORTNAME" VARCHAR(20) NOT NULL , "DESCRIPTION" VARCHAR(255), "CALENDARSEQ" BIGINT CS_FIXED NOT NULL , "PARENTSEQ" BIGINT CS_FIXED, "PERIODTYPESEQ" BIGINT CS_FIXED NOT NULL , "STARTDATE" LONGDATE CS_LONGDATE NOT NULL , "ENDDATE" LONGDATE CS_LONGDATE NOT NULL , CONSTRAINT "CS_PERIOD_PK" PRIMARY KEY ("PERIODSEQ", "REMOVEDATE")) UNLOAD PRIORITY 5  AUTO MERGE ;
CREATE UNIQUE INDEX "CS_PERIOD_AK1" ON "TCMP"."CS_PERIOD" ( "NAME" ASC, "CALENDARSEQ" ASC, "REMOVEDATE" ASC )