CREATE COLUMN TABLE "TCMP"."CS_RULE" ("TENANTID" VARCHAR(4) DEFAULT SESSION_USER, "RULESEQ" BIGINT CS_FIXED NOT NULL , "EFFECTIVESTARTDATE" LONGDATE CS_LONGDATE NOT NULL , "EFFECTIVEENDDATE" LONGDATE CS_LONGDATE NOT NULL , "ISLAST" SMALLINT CS_INT NOT NULL , "CREATEDATE" LONGDATE CS_LONGDATE NOT NULL , "REMOVEDATE" LONGDATE CS_LONGDATE NOT NULL , "CREATEDBY" VARCHAR(255) NOT NULL , "MODIFIEDBY" VARCHAR(255), "BUSINESSUNITMAP" BIGINT CS_FIXED, "CALENDARSEQ" BIGINT CS_FIXED, "EXPRESSIONTYPEFORTYPE" BIGINT CS_FIXED, "NAME" VARCHAR(127) NOT NULL , "DESCRIPTION" VARCHAR(255), "RULECONTAINER" CLOB MEMORY THRESHOLD 1000, "GOALSHEETTEMPLATE" CLOB MEMORY THRESHOLD 1000, "NOTALLOWUPDATE" SMALLINT CS_INT DEFAULT 0, "MODELSEQ" BIGINT CS_FIXED DEFAULT 0 NOT NULL , CONSTRAINT "CS_RULE_PK" PRIMARY KEY ("RULESEQ", "MODELSEQ", "REMOVEDATE", "EFFECTIVESTARTDATE"), CONSTRAINT "CS_RULE_CK" CHECK ("ISLAST" IN  ( 0, 1 ) )) UNLOAD PRIORITY 5  AUTO MERGE ;
CREATE UNIQUE CPBTREE INDEX "CS_RULE_AK1" ON "TCMP"."CS_RULE" ( "NAME" ASC, "MODELSEQ" ASC, "REMOVEDATE" ASC, "EFFECTIVESTARTDATE" ASC )