TABLEATTRIBUTE.VALUE:
UPDATE IN_ST_PAQPB SET
 TXT_ERROR_MSG = NULL
WHERE TXT_ERROR_MSG ='1';

COMMIT;

UPDATE IN_ETL_CONTROL SET
  TXT_KEY_VALUE=(SELECT LPAD(TO_CHAR(COUNT(*)),10,'0') FROM IN_ST_PAQPB),
  DTE_UPDATE_DATE=SYSDATE
WHERE TXT_KEY_STRING='PAQPB_PRESTAGE_RECORD_COUNT' AND DEC_RUNNING_FLG=1;

COMMIT;
