TABLEATTRIBUTE.VALUE:
UPDATE out_acct_detail_result_trace r SET (TXT_STATUS, DTE_UPDATE) = (SELECT 'DELETE', SYSDATE FROM DUAL) WHERE EXISTS (SELECT 1 FROM out_acct_detail d inner join IN_ETL_CONTROL ICTRLDATE on ICTRLDATE.TXT_KEY_STRING = 'OPER_CYCLE_DATE' AND ICTRLDATE.TXT_FILE_NAME = 'GLOBAL' --and d.txt_acct_type = 'P' AND d.TXT_BUSINESS_UNIT in ($$STR_BUSINESS_UNIT) AND d.DTE_CYCLE_DATE = TO_DATE(ICTRLDATE.TXT_KEY_VALUE, 'YYYY-MM-DD') inner join IN_ETL_CONTROL ICTRLTYPE ON ICTRLTYPE.TXT_KEY_STRING = '$$STR_ACCT_CYCLE_TYPE' AND ICTRLTYPE.TXT_FILE_NAME = '$$STR_PROCESSING_UNIT' AND d.DTE_CYCLE_DATE = TO_DATE(ICTRLDATE.TXT_KEY_VALUE, 'YYYY-MM-DD') AND d.TXT_CYCLE_TYPE = ICTRLTYPE.TXT_KEY_VALUE and d.txt_processingunit = ICTRLTYPE.TXT_FILE_NAME inner join IN_ETL_CONTROL ICTRLMAXDT on ICTRLMAXDT.TXT_KEY_STRING = '$$STR_MAX_CYCLE_DATE' AND ICTRLMAXDT.TXT_FILE_NAME = '$$STR_PROCESSING_UNIT' AND NVL(ICTRLMAXDT.dec_enforce_flg, 0) != 2 where r.TXT_STATUS = 'ACTIVE' AND r.dec_acct_detail_seq = d.dec_acct_detail_seq) AND TXT_STATUS = 'ACTIVE';
commit;
UPDATE in_etl_control SET txt_key_value = '0' WHERE txt_key_string IN ('ACCT_PAYMENT_CREDIT_RECORD_COUNT', 'ACCT_PAYMENT_DEBIT_RECORD_COUNT') AND txt_file_name IN ('$$STR_PROCESSING_UNIT');
COMMIT;

