CREATE COLUMN TABLE "TCMP"."CS_UNITTYPE" ("TENANTID" VARCHAR(4) DEFAULT SESSION_USER, "UNITTYPESEQ" BIGINT CS_FIXED NOT NULL , "CREATEDATE" LONGDATE CS_LONGDATE NOT NULL , "REMOVEDATE" LONGDATE CS_LONGDATE NOT NULL , "CREATEDBY" VARCHAR(255) NOT NULL , "MODIFIEDBY" VARCHAR(255), "NAME" VARCHAR(40) NOT NULL , "DESCRIPTION" VARCHAR(255), "VALUECLASSFORVALUECLASS" VARCHAR(30), "SCALE" BIGINT CS_FIXED, "COUNTRYFORCURRENCYLOCALE" VARCHAR(20), "LANGUAGEFORCURRENCYLOCALE" VARCHAR(20), "VARIANTFORCURRENCYLOCALE" VARCHAR(20), "FORMATTING" VARCHAR(30), "NOTALLOWUPDATE" SMALLINT CS_INT DEFAULT 0, "UNICODEOFSYMBOL" VARCHAR(20), "SYMBOL" NVARCHAR(10), "POSITIONOFSYMBOL" SMALLINT CS_INT DEFAULT 1, "REPORTINGSCALE" SMALLINT CS_INT DEFAULT 2, CONSTRAINT "CS_UNITTYPE_PK" PRIMARY KEY ("UNITTYPESEQ", "REMOVEDATE"), CONSTRAINT "CS_UNITTYPE_CK1" CHECK ("POSITIONOFSYMBOL" IN  ( 1, 3 ) ), CONSTRAINT "CS_UNITTYPE_CK2" CHECK ("REPORTINGSCALE" BETWEEN 0 AND 4)) UNLOAD PRIORITY 5  AUTO MERGE ;
CREATE UNIQUE CPBTREE INDEX "CS_UNITTYPE_AK1" ON "TCMP"."CS_UNITTYPE" ( "NAME" ASC, "REMOVEDATE" ASC )